use std::env;

pub struct Environment;
impl Environment {
    pub fn as_string(env_name: &'static str, default: &'static str) -> String {
        env::var(env_name).ok().unwrap_or(default.to_string())
    }
    pub fn as_u16(env_name: &'static str, default: u16) -> u16 {
        env::var(env_name)
            .ok()
            .map(|env| env.parse::<u16>().expect("Failed to parse to u16"))
            .unwrap_or(default)
    }
    pub fn as_u32(env_name: &'static str, default: u32) -> u32 {
        env::var(env_name)
            .ok()
            .map(|env| env.parse::<u32>().expect("Failed to parse to u32"))
            .unwrap_or(default)
    }

    pub fn as_i16(env_name: &'static str, default: i16) -> i16 {
        env::var(env_name)
            .ok()
            .map(|env| env.parse::<i16>().expect("Failed to parse to i16"))
            .unwrap_or(default)
    }

    const JWT_PRIVATE_KEY: &'static str = "";
    const JWT_PUBLIC_KEY: &'static str = "";
    
    pub fn jwt_public_key() -> String {
        Self::as_string("BASE64_JWT_PUBLIC_KEY", Self::JWT_PUBLIC_KEY)
    }
    
    pub fn jwt_private_key() -> String {
        Self::as_string("BASE64_JWT_PRIVATE_KEY", Self::JWT_PRIVATE_KEY)
    }
}
